package java_lhh_day02;

public class AssignmentOperatorEx01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//복합 대입 연산자 예제
		// A = A+B
		// A +=B
		int num1 = 10, num2=20;
		
		num1= num1+num2;
		
		System.out.println(num1);
		
		int num3= 10, num4=20;
		
		num3+=num4;
		
		System.out.println(num3);
		
		
	}

}
